assets.content:
html {
  font-size: 16px;
}

@media (max-width: 1366px) {
  html {
    font-size: 1.17vw;
  }
}

@media (max-width: 432px) {
  html {
    font-size: 3.4vw;
  }
}

body {
  font-weight: 400;
  font-style:normal;
  text-decoration: none;
  text-transform: none;
  letter-spacing: normal;
  line-height: 1.8;

  background-color: #ffffff;
  font-family: Roboto;
  color: #000000;
}

button {
  background-color: #ffffff;
}

query.content:
query GetPublicJobs($getPublicJobsInput: GetPublicJobsInput!) {
              getPublicJobs(getPublicJobsInput: $getPublicJobsInput) {
                data {
                  id
                  title
                  companyAccount {
                    name
                  }
                  url
                  roleDescription
                  dutyTime
                }
                metadata {
                  page
                  perPage
                  pageCount
                  count
                }
              }
            }

query getCompanyPublicContactData($companyId: ID!){
    getCompanyPublicContactData(companyId: $companyId){
        name
        street
        houseNumber
        postalCode
        city
        contactEmail
        contactPhone
    }
}
